import React, { useState, useEffect } from 'react';
import './Table.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Dropdown from 'react-bootstrap/Dropdown';
import Button from 'react-bootstrap/Button';
import TableComponent from './custom/TableComponent';

const Table = () => {
    const [formData, setFormData] = useState({
        alcoholF: false,
        vegieAndFruitF: false,
        psychologicalF: false,
        hypertenseF: false,
        diabetesF: false,
        smokingF: false,
        dyslipidemicF: false,
        obesityF: false,
        physicalF: false,
    });

    const [selectedFactors, setSelectedFactors] = useState(["", "", ""]);
    const [mapData, setMapData] = useState({});

    useEffect(() => {
        console.log('Updated mapData:', mapData);
    }, [mapData]);

    const handleChange = (selectedValue, index) => {
        if (selectedValue === "") {
            const newSelectedFactors = [...selectedFactors];
            newSelectedFactors[index] = "";
            setSelectedFactors(newSelectedFactors);
            setFormData({ ...formData, [selectedFactors[index]]: false });
        } else if (!selectedFactors.includes(selectedValue)) {
            const newSelectedFactors = [...selectedFactors];
            setFormData({ ...formData, [selectedFactors[index]]: false });
            newSelectedFactors[index] = selectedValue;
            setSelectedFactors(newSelectedFactors);
            setFormData({ ...formData, [selectedValue]: true });
        } else {
            alert("You have already selected this risk factor.");
        }
    };

    const submit = async (e) => {
        e.preventDefault();
        console.log('Submitting formData:', formData);

        try {
            const response = await fetch('http://192.168.68.114:5085/TableFactorCompute', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (response.ok) {
                const regionData = await response.json();
                console.log('Received regionData:', regionData);
                setMapData(regionData); // This will trigger a re-render
            } else {
                console.error('Failed to submit form data');
            }
        } catch (error) {
            console.error('Error during form submission', error);
        }
    };

    return (
        <div className="table-page">
            <form className="choose-risk-factors-section" onSubmit={submit}>
                {/* Form content here */}

                <Button 
                    as="input" 
                    type="submit"
                    value="Vyhodnotit" 
                    style={{
                        borderRadius: '15px',
                        fontSize: '28px',                            
                        height: '60px',
                        width: '250px',
                        marginBottom: '10px',
                    }}
                />
            </form>

            <div className="table-section">
                <div className='table-header'>
                    <h2>Výsledek vyhledávání</h2>
                </div>

                <div className="big-element">
                    <TableComponent serverResponse={mapData} />
                </div>
            </div>
        </div>
    );
};

export default Table;
