import React, { useState, useEffect } from 'react';
import './MapModel.css'; // Import your CSS file for styling
import { ReactComponent as CzechRepublicMap } from './cz.svg';

const regionData = {
  "CZ10":  {value: true} , // praha
  "CZ63": {value: false} , // vysocina
  "CZ42": {value: false}, // usti
  "CZ31": {value: false}, // jihocesky
  "CZ64": {value: false}, // jihomoravsky
  "CZ41": {value: false}, // karlovarsky
  "CZ52": {value: false}, // hradec
  "CZ51": {value: false}, // liberec
  "CZ80": {value: false}, // moravslezsky
  "CZ71": {value: false}, // omlomouc
  "CZ53": {value: false}, // pardub
  "CZ32": {value: false}, // plzen
  "CZ20": {value: false}, // stredocesky
  "CZ72": {value: false}, // zlin
};

const getColor = (value) => {
  return value === true ? '#51ff00' : '#D25C5C'; // green : red 
};

const MapPage = () => {
  const [regionColors, setRegionColors] = useState({});

  useEffect(() => {
    const updatedColors = {};
    Object.keys(regionData).forEach((regionId) => {
      const { value } = regionData[regionId];
      updatedColors[regionId] = getColor(value);
    });
    setRegionColors(updatedColors);
  }, []);

  return (
    <div>
      <div>
        <CzechRepublicMap className="czech-map" />
      </div>

      <style jsx="true">{`
        .czech-map #CZ10 {
          fill: ${regionColors["CZ10"] || 'gray'};
        }
        .czech-map #CZ63 {
          fill: ${regionColors["CZ63"] || 'gray'};
        }
        .czech-map #CZ52 {
            fill: ${regionColors["CZ52"] || 'gray'};
          }

        .czech-map #CZ42 {
            fill: ${regionColors["CZ42"] || 'gray'};
          }
        .czech-map #CZ31 {
            fill: ${regionColors["CZ31"] || 'gray'};
          }
        .czech-map #CZ64 {
            fill: ${regionColors["CZ64"] || 'gray'};
          }
        .czech-map #CZ41 {
            fill: ${regionColors["CZ41"] || 'gray'};
          }
        .czech-map #CZ51 {
            fill: ${regionColors["CZ51"] || 'gray'};
          }
        .czech-map #CZ80 {
            fill: ${regionColors["CZ80"] || 'gray'};
          }
        .czech-map #CZ71 {
            fill: ${regionColors["CZ71"] || 'gray'};
          }
        .czech-map #CZ53 {
            fill: ${regionColors["CZ53"] || 'gray'};
          }
        .czech-map #CZ32 {
            fill: ${regionColors["CZ32"] || 'gray'};
          }
        .czech-map #CZ20 {
            fill: ${regionColors["CZ20"] || 'gray'};
          }
        .czech-map #CZ72 {
            fill: ${regionColors["CZ72"] || 'gray'};
          }
      `}</style>

    </div>
  );
};

export default MapPage;
