import React, { useState, useEffect } from 'react';
//import './Map.css'; // Import your CSS file for styling
import { ReactComponent as CzechRepublicMap } from './cz.svg';

const regionData = {
    "region-1": { value: 77, average: 56 }, // Example data
    "region-2": { value: 40, average: 56 },
    // Add more regions as needed
};

const getColor = (value, average) => {
    return value > average ? 'green' : 'red';
};

/*
