    width: 600,
    height: 600
};
*/



const MapPage = () => {
    const [regionColors, setRegionColors] = useState({});

    useEffect(() => {
        const updatedColors = {};
        Object.keys(regionData).forEach((regionId) => {
            const { value, average } = regionData[regionId];
            updatedColors[regionId] = getColor(value, average);
        });
        setRegionColors(updatedColors);
    }, []);


    return (
        <div>
            <div style={mapcontainer}>
                <CzechRepublicMap style={czechmap} />
            </div>
            <style jsx>{`
        .czech-map #region-1 {
          fill: ${regionColors["region-1"] || 'gray'};
        }
        .czech-map #region-2 {
          fill: ${regionColors["region-2"] || 'gray'};
        }
        /* Add more regions as needed */
      `}</style>
        </div>
    );
};

export default MapPage;
