import React, { useState, useEffect } from 'react';
import './MapModel.css'; // Import your CSS file for styling
import { ReactComponent as CzechRepublicMap } from './cz.svg';

const regionData = {
  "CZ10": { value: 1000, average: 56 }, // praha
  "CZ63": { value: 40, average: 56 }, // vysocina
  // "CZ42" usti
  // "CZ31" jihocesky
  // "CZ64" jihomoravsky
  // "CZ41" karlovarsky
  // "CZ52" hradec
  // "CZ51" liberec
  // "CZ80" moravslezsky
  // "CZ71" omlomouc
  // "CZ53" pardub
  // "CZ32" plzen
  // "CZ20" stredocesky
  // "CZ72" zlin
};

const getColor = (value, average) => {
  return value > average ? '#0FCEA0' : '#D25C5C'; // green : red 
};

const MapPage = () => {
  const [regionColors, setRegionColors] = useState({});

  useEffect(() => {
    const updatedColors = {};
    Object.keys(regionData).forEach((regionId) => {
      const { value, average } = regionData[regionId];
      updatedColors[regionId] = getColor(value, average);
    });
    setRegionColors(updatedColors);
  }, []);

  return (
    <div>
      <div>
        <CzechRepublicMap className="czech-map" />
      </div>

      <style jsx="true">{`
        .czech-map #CZ10 {
          fill: ${regionColors["CZ10"] || 'gray'};
        }
        .czech-map #CZ63 {
          fill: ${regionColors["CZ63"] || 'gray'};
        }
        .czech-map #CZ52 {
            fill: ${regionColors["CZ52"] || 'gray'};
          }

        .czech-map #CZ42 {
            fill: ${regionColors["CZ42"] || 'gray'};
          }
        .czech-map #CZ31 {
            fill: ${regionColors["CZ31"] || 'gray'};
          }
        .czech-map #CZ64 {
            fill: ${regionColors["CZ64"] || 'gray'};
          }
        .czech-map #CZ41 {
            fill: ${regionColors["CZ41"] || 'gray'};
          }
        .czech-map #CZ51 {
            fill: ${regionColors["CZ51"] || 'gray'};
          }
        .czech-map #CZ80 {
            fill: ${regionColors["CZ80"] || 'gray'};
          }
        .czech-map #CZ71 {
            fill: ${regionColors["CZ71"] || 'gray'};
          }
        .czech-map #CZ53 {
            fill: ${regionColors["CZ53"] || 'gray'};
          }
        .czech-map #CZ32 {
            fill: ${regionColors["CZ32"] || 'gray'};
          }
        .czech-map #CZ20 {
            fill: ${regionColors["CZ20"] || 'gray'};
          }
        .czech-map #CZ72 {
            fill: ${regionColors["CZ72"] || 'gray'};
          }
      `}</style>

    </div>
  );
};

export default MapPage;
