import React from 'react';
import ToggleComponent from './custom/ToggleComponent.js';

const RizikoveFaktory = () => {
    return (
        <div className="max-w-3xl mx-auto p-6 bg-white shadow-lg rounded-lg mt-10">
            <h2 className="text-3xl font-semibold text-gray-800 text-center mb-6">Proč je prevence důležitá?</h2>
            <p className="text-gray-600 text-lg leading-relaxed text-center">
                Základem pevného zdraví do vysokého věku je systematická prevence ve všech aspektech zdravého životního stylu. Prevence jako taková je soubor opatření, která by měla zabránit vzniku a rozvinutí onemocnění a zamezit nepříznivým následkům nemoci vedoucí k předčasnému úmrtí. Pečlivě si prostudujte níže uvedené informace o rizikových faktorech.
            </p>
            
            <hr className="border-gray-300 my-6" />
            
            <div className="space-y-4">
                <ToggleComponent title="HYPERTENZE - VYSOKÝ KREVNÍ TLAK" initialOpenState={false} content="TBA" />
                <ToggleComponent title="NADMĚRNÁ KONZUMACE ALKOHOLU" initialOpenState={false} content="TBA" />
                <ToggleComponent 
                    title="DIABETES MELLITUS" 
                    initialOpenState={false} 
                    content="Cukrovka nás může postihnout v jakémkoliv věku, kdy pouze zlomek všech diabetiků si nemoc rozvine na základě generačních faktorů..."
                />
                <ToggleComponent title="NEDOSTATEČNÁ KONZUMACE OVOCE A ZELENINY" initialOpenState={false} content="TBA" />
                <ToggleComponent title="PSYCHOSOCIÁLNÍ FAKTORY" initialOpenState={false} content="TBA" />
                <ToggleComponent title="KOUŘENÍ" initialOpenState={false} content="TBA" />
                <ToggleComponent title="DYSLIPIDÉMIE" initialOpenState={false} content="TBA" />
                <ToggleComponent title="OBEZITA" initialOpenState={false} content="TBA" />
                <ToggleComponent title="FYZICKÁ NEČINNOST" initialOpenState={false} content="TBA" />
            </div>
        </div>
    );
};

export default RizikoveFaktory;
