import React, { useState, useEffect } from 'react';
import './MapModel.css'; // Import your CSS file for styling
import { ReactComponent as CzechRepublicMap } from './cz.svg';

const regionData = {
  "CZ10": { value: 1000, average: 56 }, // Example data
  "region-2": { value: 40, average: 56 },
  // Add more regions as needed
};

const getColor = (value, average) => {
  return value > average ? 'green' : 'red';
};

const MapPage = () => {
  const [regionColors, setRegionColors] = useState({});

  useEffect(() => {
    const updatedColors = {};
    Object.keys(regionData).forEach((regionId) => {
      const { value, average } = regionData[regionId];
      updatedColors[regionId] = getColor(value, average);
    });
    setRegionColors(updatedColors);
  }, []);

  return (
    <div className="map-page">
      <div className="map-container">
        <CzechRepublicMap className="czech-map" />
      </div>


    </div>
  );
};

export default MapPage;
