import React, { useState, useEffect } from 'react';
import { Checkbox } from 'pretty-checkbox-react';
import './Map.css'; // Import your CSS file for styling
import Mapomponent from './custom/MapComponent';


const MapPage = () => {
    const [formData, setFormData] = useState({
        alcoholF: false,
        vegieAndFruitF: false,
        psychologicalF: false,
        hypertenseF: false,
        diabetesF: false,
        smokingF: false,
        dyslipidemicF: false,
        obesityF: false,
        physicalF: false,
    });

    const [selectedOrder, setSelectedOrder] = useState([]);
    const [visibleFactors, setVisibleFactors] = useState({
        1: Object.keys(formData),
        2: Object.keys(formData),
        3: Object.keys(formData),
    });
    const [lockedColumns, setLockedColumns] = useState({ 1: false, 2: false, 3: false });

    const handleChange = (e, columnIndex) => {
        const name = e.target.name;
        const updatedValue = !formData[name];

        if (updatedValue) {
            if (selectedOrder.length < 3) {
                setSelectedOrder([...selectedOrder, name]);
                setFormData({ ...formData, [name]: updatedValue });
                setLockedColumns({ ...lockedColumns, [columnIndex]: true });
            } else {
                alert("You can select up to three risk factors.");
            }
        } else {
            setSelectedOrder(selectedOrder.filter(item => item !== name));
            setFormData({ ...formData, [name]: updatedValue });
            setLockedColumns({ ...lockedColumns, [columnIndex]: false });
        }

        updateVisibleFactors(columnIndex, name, updatedValue);
        console.log('Aktualni hodnota pole ' + name + ' je: ' + updatedValue);
    };

    const updateVisibleFactors = (columnIndex, name, updatedValue) => {
        const updatedVisibleFactors = { ...visibleFactors };

        if (updatedValue) {
            for (let i = columnIndex + 1; i <= 3; i++) {
                updatedVisibleFactors[i] = updatedVisibleFactors[i].filter(factor => factor !== name);
            }
        } else {
            for (let i = columnIndex + 1; i <= 3; i++) {
                if (!updatedVisibleFactors[i].includes(name)) {
                    updatedVisibleFactors[i].push(name);
                }
            }
        }

        setVisibleFactors(updatedVisibleFactors);
    };

    useEffect(() => {
        // Fetch data from the endpoint and filter based on formData values
        // Add your data fetching logic here
    }, [formData]);

    const factorLabels = {
        physicalF: 'Fyzická nečinnost',
        alcoholF: 'Nadměrná konzumace alkoholu',
        diabetesF: 'Diabetes mellitus',
        smokingF: 'Kouření',
        vegieAndFruitF: 'Nedostatečná konzumace ovoce a zeleniny',
        psychologicalF: 'Psychosociální faktory',
        hypertenseF: 'Hypertenze',
        dyslipidemicF: 'Dyslipidémie',
        obesityF: 'Obezita'
    };

    const isCheckboxDisabled = (name, columnIndex) => {
        // Disable checkboxes in the same column if one is selected, and in subsequent columns if the factor is selected in previous columns
        if (lockedColumns[columnIndex] && !formData[name]) {
            return true;
        }
        for (let i = 1; i < columnIndex; i++) {
            if (selectedOrder.includes(name)) {
                return true;
            }
        }
        return false;
    };

    return (
        <div className="map-page">
            <div className="tip-section">
                <p className="tip-text">
                    <strong>TIP:</strong> Rizikové faktory je možno vybírat pouze tři najednou. Pro odebrání rizikového faktoru
                    odškrtněte daný rizikový faktor. Doporučujeme si pro výběr vybrat pro Vás nejdůležitější první
                    faktor a následně dle možnosti vyzkoušet a kombinovat dostupné další rizikové faktory. Výsledky
                    se zobrazují graficky na mapě v dolní části stránky.
                </p>
                <button className="detail-button">Zobrazit detailnější data</button>
            </div>

            <div className="checkbox-section">
                {[1, 2, 3].map(columnIndex => (
                    <div className="checkbox-column" key={columnIndex}>
                        {visibleFactors[columnIndex].map((key) => (
                            <label key={key}>
                                <Checkbox
                                    shape="round"
                                    variant="fill"
                                    name={key}
                                    checked={formData[key]}
                                    onChange={(e) => handleChange(e, columnIndex)}
                                    disabled={isCheckboxDisabled(key, columnIndex)}
                                />
                                {factorLabels[key]}
                            </label>
                        ))}
                    </div>
                ))}
            </div>

            <div className="map-section">

                <div className='map-header'>
                    <h2 style={{ color: 'white' }}>MAPA RIZIKOVÝCH FAKTORŮ</h2>
                </div>

                <div className='container'>
                    <div className="legend">
                        <h3>Legenda</h3>
                        <p>Vybrané rizikové faktory</p>
                        <ul>
                            <li>nedostatečná konzumace ovoce a zeleniny</li>
                            <li>psychosociální faktory</li>
                            <li>kouření</li>
                            <li>Hypertenze</li>
                        </ul>
                        <div className="legend-colors">
                            <p className="color-1">Krajový průměr je větší než celostátní</p>
                            <p className="color-2">Krajový průměr je menší než celostátní</p>
                        </div>
                    </div>

                    <div className="map-container">
                        <Mapomponent />
                    </div>

                </div>
            </div>






                <div className="container">
                    <div className="left-container">
                        <div className="small-element">Small Element 1</div>
                        <div className="small-element">Small Element 2</div>
                    </div>
                    <div className="big-element">Big Element</div>
                </div>





        </div>
    );
};

export default MapPage;
